export default {
  data: function data() {
    return {
      imageData: null,
      valid: false,
      ot: 'rgba(0,0,0,0.5)',
      ob: 'rgba(250,250,250,1)',
      uname: '',
      upass: '',
      name: '',
      rank: '',
      reol: false,
      reolb: false,
      checkbox1: false,
      checkbox2: false,
      checkbox3: false,
      checkbox4: false,
      icons: {
        fliph: 'mdi-swap-horizontal',
        flipv: 'mdi-swap-vertical',
        rotate: 'mdi-format-rotate-90',
        upload: 'mdi-cloud-upload'
      }
    };
  },
  methods: {
    create: function create() {
      this.axios({
        url: 'register',
        method: 'post',
        data: {
          uname: this.uname,
          upass: this.upass,
          name: this.name,
          rank: this.rank,
          dp: this.imageData ? this.imageData : '',
          per_accounts: this.checkbox2 ? 1 : 0,
          per_billing: this.checkbox3 ? 1 : 0,
          per_clients: this.checkbox1 ? 1 : 0,
          per_lab: this.checkbox4 ? 1 : 0
        }
      }).then(function (response) {
        if (response.data.success) {
          alert('User has been added');
        } else {
          alert(response.data.error);
        }
      });
    }
  },
  watch: {
    imageData: function imageData(val) {
      this.reolb = val != null;

      if (val == null) {
        this.reol = false;
      }
    },
    reol: function reol(val) {
      if (val) {
        this.reolb = false;
      }
    }
  }
};
